import {React,useMemos} from 'react'
import { connect,} from 'react-redux'
import { bindActionCreators } from 'redux'


const ADD_TODO = 'ADD_TODO'
const REMOVE_TODO = 'REMOVE_TODO'

// Action Creators:
function addTodo (todo) {
	return {type: 'ADD_TODO', payload: todo };
}

function removeTodo (todo) {
	return {type: 'REMOVE_TODO', payload: todo };
}


// State:
const initialData = {
  1: { id: 1, text: "brush a tooth"},
  2: { id: 2, text: "wash a face"},
  3: { id: 3, text: "drink a coffee"},
}

const initialState = {
  index: Object.values(initialData).length,
  data: initialData
}

// Reducer:
const reducer = (state = initialState, action) => {
  let newState = {...state}
  const { id, text } = action.payload || {}
  switch (action.type) {
  	case ADD_TODO:
      newState.index++
      newState.data[newState.index] = { id: newState.index, text };
      return newState
    case REMOVE_TODO:
      let newData = {...newState.data}
      delete newData[id]
      return {
        ...newState,
        data: newData
      }
    default:
    	return state;
  }
}




// Presentational Components (No state, only props and render)
const List = ({data, onRemove}) => {
  const list = Object.values(data)
  return (
  <ul>
    {
        list.map(
          todo =>
            <TodoItem
              key={todo.id}
              todo={todo}
              onRemove={onRemove}
            />
        )
    }
  </ul>
 )
}

function TodoItem({todo, onRemove}) {
  const _onRemove = () => onRemove(todo)
	return <li>{todo.text} <button onClick={_onRemove}>Remove</button></li>;
}

