import React,{ useState,Component } from 'react'
import { connect } from 'react-redux'
import { GlobalStyle } from './styles/GlobalStyle'
import {textChange} from './actions/actions'


const App = () => {
  return (
    <React.Fragment>
      <div>文字数：{this.props.textLength}</div>
      <textarea
        type='text'
        onClick={e => this.props.textChange(e.target.value, e.target.value.length)}
      />
    </React.Fragment>
  );
};
