import {ADD_TODO} from '../actions/actions'
import {REMOVE_TODO} from '../actions/actions'


const initialData = {
  1: {id:1,text: 'bush a tooth'},
  2: {id:2,text: 'wash a face'},
  3: {id:3,text: 'drink a coffee'}
}

const initialState = {
  index: Object.values(initialData).length,
  data: initialData
}

const todo = (state = initialState,action) => {
    switch(action.type) {
        case TEXTCHANGE:
          return {textValue: action.textValue,textLength: action.textLength};
        default:
            return state;
    }
}

export default text
